mapper.select:
 	    SELECT *
		FROM (
		        SELECT rownum rn ,  a.*
		        FROM  (select * from emp order by empno) a
		     )
		WHERE rn between #{start} AND  #{end}

mapper.update:
UPDATE emp
SET ename = #{ename}, job = #{job}, sal = #{sal}, hiredate = #{hiredate}, 
comm = #{comm}, mgr = #{mgr}, deptno = #{deptno}
WHERE empno = #{empno}
